                <h2>Nový článek</h2>
            </div>
            <div className="row">
                <label htmlFor="name-filed">Název: </label>
                <input className="form-control" id="name-filed" type="text" value={name} onChange={(e) => setName(e.target.value)}></input>
                <label htmlFor="subject-filed">Předmět: </label>
