import { useState } from "react";
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';
import Toast from 'react-bootstrap/Toast';

function ToastModal(props) {
  return (
    <Toast>
      <Toast.Header>
        <img src="holder.js/20x20?text=%20" className="rounded me-2" alt="" />
        <strong className="me-auto">Chyba</strong>
        <small>11 mins ago</small>
      </Toast.Header>
      <Toast.Body>Hello, world! This is a toast message.</Toast.Body>
    </Toast>
  );
}

function MyVerticallyCenteredModal(props) {
    return (
      <Modal
        {...props}
        size="lg"
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            {props.title}
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <h4>{props.subject}</h4>
          <p>
            {props.body}
          </p>
        </Modal.Body>
        <Modal.Footer>
          <Button onClick={props.onHide}>Zav≈ô√≠t</Button>
        </Modal.Footer>
      </Modal>
    );
  }

function NewArticle(props) {
    const [modalShow, setModalShow] = useState(false);
    const [showA, setShowA] = useState(false);
    const toggleShowA = () => setShowA(!showA);

    const [name, setName] = useState("");
    const [subject, setSubject] = useState("");
    const [author, setAuthor] = useState("");
    const [text, setText] = useState("");

    const addItemButtonPressed = () => {
        if (name && subject && author && text) {
            props.save_item({name : name, subject : subject, author : author, text : text});
            setName("");
            setSubject("");
            setAuthor("");
            setText("");

            setModalShow(true);
        } else {
toggleShowA();
        }
    }



    return (
        <div className="container">
            <div className="row">
                <h2>Nov√Ω ƒçl√°nek</h2>
            </div>
            <div className="row">
                <label htmlFor="name-filed">N√°zev: </label>
                <input className="form-control" id="name-filed" type="text" value={name} onChange={(e) => setName(e.target.value)}></input>
                <label htmlFor="subject-filed">P≈ôedmƒõt: </label>
                <input className="form-control" id="subject-filed" type="text" value={subject} onChange={(e) => setSubject(e.target.value)}></input>
                <label htmlFor="author-filed">Autor: </label>
                <input className="form-control" id="author-filed" type="text" value={author} onChange={(e) => setAuthor(e.target.value)}></input>
                <label htmlFor="text-filed">Text: </label>
                <input className="form-control" id="text-filed" type="text" value={text} onChange={(e) => setText(e.target.value)}></input>
            </div>
            <div className="row mt-3">
                <button className="btn btn-secondary" type="button" onClick={addItemButtonPressed}>P≈ôidat</button>
            </div>
            <MyVerticallyCenteredModal
                show={modalShow}
                onHide={() => setModalShow(false)}
                title={"Hotovo"}
                body={"Ulo≈æeno"}
            />
if (showA) {
            <ToastModal />}
        </div>

    )
}
export default NewArticle;
