import { Routes, Route, Link, BrowserRouter } from 'react-router-dom';
import { useState, useEffect } from "react";
import axios from 'axios';


import NavScrollExample from './pages/NavScrollExample';

import Home from './pages/Home';
import Contact from './pages/Contact';
import NoPage from './pages/NoPage';
import ArticleArray from './pages/Article_array';
import NewArticle from './pages/NewArticle';

function App() {
  const [data, setData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('http://127.0.0.1:8000/api/get_items');
        setData(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  const save_items = (data) => {
    console.log(data);
  };

  return (
      <BrowserRouter>
        <NavScrollExample />
        <div className='conainer' style={{paddingLeft: "40px"}}>
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path='/article_array' element={<ArticleArray items={data}/>} />
            <Route path='/new_article' element={<NewArticle save_item={save_items}/>} />
            <Route path="/contact" element={<Contact />} />
            <Route path="*" element={<NoPage />} />
          </Routes>

          {data.map((item) => (
        <div key={item.id}>
          <h3>{item.name}</h3>
          <p>{item.subject}</p>
          <p>{item.autor}</p>
          <p>{item.text}</p>
          <p>{item.date_time}</p>
        </div>
      ))}
        </div>
      </BrowserRouter>
  );
}

export default App;
